#!/usr/bin/env python3
# RSA wiener attack
# https://github.com/orisano/owiener
# https://en.wikipedia.org/wiki/Continued_fraction
# Author Dario Clavijo 2020
# GPLv3

import gmpy2

def cont_frac(a,b):
  coef=[]
  i = a//b
  while b != 0:
    r = a//b
    i = r
    coef.append(i)
    f = a - i*b
    a = b
    b = f
  return coef

def convergents(e):
    n = [] # Nominators
    d = [] # Denominators

    for i in range(len(e)):
        if i == 0:
            ni = e[i]
            di = 1
        elif i == 1:
            ni = e[i]*e[i-1] + 1
            di = e[i]
        else: # i > 1
            ni = e[i]*n[i-1] + n[i-2]
            di = e[i]*d[i-1] + d[i-2]

        n.append(ni)
        d.append(di)
        yield (ni, di)

def quadratic_solve(a,b,c):
  disc = (b**2) - (4*a*c)
  if disc >0:
    a2 = (2*a)
    x0= (-b + gmpy2.isqrt(disc))//a2
    x1= (-b - gmpy2.isqrt(disc))//a2
    return x0,x1

def wiener(e,N):
  tmp = []
  for pk,pd in convergents(cont_frac(e,N)):
    if pk > 0:
      phi = (e*pd - 1)//pk
      b = N-phi+1 # (p-1)*(q-1) = (p*q)-(p-q)+1 = N-(p-q)+1 
      x = quadratic_solve(1,-b,N)
      if x != None:
        if N % x[0] == 0 or N % x[1] == 0:
          return x

